SELECT t1.id AS id, t1.userId AS userId, t2.userName AS userName, t2.sex AS sex, t2.homeId AS homeId, t3.homeName AS homeName, t2.groupId AS groupId, t4.groupName AS groupName, t4.districtId AS districtId, t5.districtName AS districtName, t6.churchName AS churchName, t5.churchId AS churchId, t1.gospel AS gospel, t1.meeting AS meeting, t1.meetingType AS meetingType, t1.morningRevial AS morningRevial, t1.oneNew AS oneNew, t1.oneNewNum AS oneNewNum, t1.personalPray AS personalPray, t1.prophesy AS prophesy, t1.PSRP AS PSRP, t1.shepherd AS shepherd, t1.threeOld AS threeOld, t1.threeOldNum AS threeOldNum, t1.time AS time, t1.twoAltarPray AS twoAltarPray FROM t_recoverylife t1, t_userinfo t2, t_home t3, t_group t4, t_district t5, t_church t6 WHERE t1.userId = t2.userId AND t2.homeId = t3.id AND t2.groupId = t4.id AND t4.districtId = t5.id AND t5.churchId = t6.id
AND t2.userId=#{userid,jdbcType=VARCHAR}
and t1.time >= #{startTime, jdbcType=DATE}
and t1.time <= #{endTime, jdbcType=DATE}
limit #{dataStartNum, jdbcType=INTEGER}, #{dataCount, jdbcType=INTEGER}

SELECT t1.id AS id, t1.userId AS userId, t2.userName AS userName, t2.sex AS sex, t2.homeId AS homeId, t3.homeName AS homeName, t2.groupId AS groupId, t4.groupName AS groupName, t4.districtId AS districtId, t5.districtName AS districtName, t6.churchName AS churchName, t5.churchId AS churchId, t1.gospel AS gospel, t1.meeting AS meeting, t1.meetingType AS meetingType, t1.morningRevial AS morningRevial, t1.oneNew AS oneNew, t1.oneNewNum AS oneNewNum, t1.personalPray AS personalPray, t1.prophesy AS prophesy, t1.PSRP AS PSRP, t1.shepherd AS shepherd, t1.threeOld AS threeOld, t1.threeOldNum AS threeOldNum, t1.time AS time, t1.twoAltarPray AS twoAltarPray FROM t_recoverylife t1, t_userinfo t2, t_home t3, t_group t4, t_district t5, t_church t6 WHERE t1.userId = t2.userId AND t2.homeId = t3.id AND t2.groupId = t4.id AND t4.districtId = t5.id AND t5.churchId = t6.id
AND t2.userId=#{userid,jdbcType=VARCHAR}
